import React from 'react'
import ReactDOM from 'react-dom'

const Header = (props) => { //rendering name of course
  return (
    <h1>{props.course}</h1>
  )
}

const Content = (props) => { //renders parts and number of exercises
  const elements = [...props.data];
  return (
    <>
        <Part data = {elements[0]}/>
        <Part data = {elements[1]}/>
        <Part data = {elements[2]}/>
    </>
  )
}

const Part = (props) => {
  return (
    <p>{props.data.part} {props.data.exercise}</p>
  )
}

const Total = (props) => { //renders total number of exercises
  return (
    <p>Number of exercises {props.total}</p>
  )
}

const App = () => {
  const course = 'Half Stack application development'
  const part1 = {name:'Fundamentals of React', exercises: 10};
  const part2 = {name:'Using props to pass data', exercises: 7};
