import React from 'react'
import ReactDOM from 'react-dom'

const Header = (props) => { //rendering name of course
  return (
    <h1>{props.course}</h1>
  )
}

const Content = (props) => { //renders parts and number of exercises
  const numbers = [];
  let id = 0;
  props.data.forEach(data => {
    id += 1;
    numbers.push(id);
  })
  const elements = props.data.map(dataObject => {
    return (
      <p>{dataObject.part1} {dataObject.exercises1}</p>
    )
  })
  return (
    <>
      {elements}
    </>
  )
}

const Total = (props) => { //renders total number of exercises
  return (
    <p>Number of exercises {props.total}</p>
  )
}

const App = () => {
  const course = 'Half Stack application development'
  const part1 = 'Fundamentals of React'
  const exercises1 = 10
  const part2 = 'Using props to pass data'
  const exercises2 = 7
  const part3 = 'State of a component'
  const exercises3 = 14
  const contentData = [{part1, exercises1}, {part2, exercises2}, {part3, exercises3}];

  return (
    <div>
      <Header course = {course}/>
      <Content data = {contentData}/>
      <Total total = {exercises1 + exercises2 + exercises3}/>
    </div>
  )
}

ReactDOM.render(<App />, document.getElementById('root'))
